mapper.select:
SELECT B.* FROM( SELECT ROWNUM RNUM, A.* FROM( SELECT REP_NO, BO_NO, REP_CONTENT, REP_WRITER, TO_CHAR(REP_DATE, 'YYYY-MM-DD HH:MI') REP_DATE, REP_IP FROM REPLY2
ORDER BY REP_NO DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE REPLY2
SET
REP_CONTENT = #{rep_content}
WHERE REP_NO = #{rep_no} AND BO_NO = #{bo_no} AND REP_PASS = #{rep_pass}
